import React from 'react';
import Grid from '@material-ui/core/Grid';
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';

export default function Providers() {
  const [provider, setProvider] = React.useState('aws');
  const [anchorEl, setAnchorEl] = React.useState(null);

  const regions = [
    'Show some love to Material-UI',
    'Show all notification content',
    'Hide sensitive notification content',
    'Hide all notification content',
  ];

  const handleChange = (event, newAlignment) => {
    setProvider(newAlignment);
  };

  const handleClickListItem = (event) => {
    setAnchorEl(event.currentTarget);
  }

  if (this.props.provider === "aws") {
    provider = <div>
      <h3>AWS</h3>
      Region
      <List component="nav" aria-label="Region">
        <ListItem
          button
          aria-haspopup="true"
          aria-controls="lock-menu"
          aria-label="AWS Region"
          onClick={handleClickListItem}
        >
          <ListItemText primary="us-west-1" secondary={options[selectedIndex]} />
        </ListItem>
      </List>
    </div>
  }

  return (
    <div className="providers">
      <h2>Select Cloud Provider</h2>
      <Grid container spacing={2} direction="column" alignItems="center">
        <Grid item>
          <ToggleButtonGroup
            value={provider}
            exclusive
            onChange={handleChange}
            aria-label="Cloud Provider"
          >
            <ToggleButton key={1} value="aws" aria-label="Amazon AWS">
              Amazon AWS
                    </ToggleButton>
            <ToggleButton key={2} value="gcp" aria-label="Google Cloud">
              Google Cloud
                    </ToggleButton>
            <ToggleButton key={3} value="azure" aria-label="Microsoft Azure">
              Microsoft Azure
                    </ToggleButton>
          </ToggleButtonGroup>
          {providerBox}

        </Grid>
      </Grid>
    </div>
  );
}
