import React from 'react';
import './Providers.css';
import Button from '@material-ui/core/Button';
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import Grid from '@material-ui/core/Grid';


class Providers extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            provider: "aws"
        };
    };

    setProvider = (event, value) => {
        this.setState({ provider: value });
    };

    render() {
        return (
            <div className="providers">
                <h2>Select Cloud Provider</h2>
                <Grid container spacing={2} direction="column" alignItems="center">
                    <Grid item>
                    <ToggleButtonGroup
                        value={this.props.provider}
                        exclusive
                        onChange={this.setProvider}
                        aria-label="Cloud Provider"
                    >
                        <ToggleButton value="aws" aria-label="Amazon AWS">
                            Amazon AWS
                        </ToggleButton>
                        <ToggleButton value="gcp" aria-label="Google Cloud">
                            Google Cloud
                        </ToggleButton>
                        <ToggleButton value="right" aria-label="Microsoft Azure">
                            Microsoft Azure
                        </ToggleButton>
                    </ToggleButtonGroup>
                    </Grid>
                </Grid>
            </div>
        );
    }
}

export default Providers;
